import {IS_LOGGED} from '../../Action/action-type';
import {USER_INFO} from '../../Action/action-type';
import {MODIFY_USER} from '../../Action/action-type';

const initialState = {
    isLoggedIn: false,
    user: null
}

export default function userReducer(state = initialState, action) {
	switch(action.type) {
		case IS_LOGGED:
			return {
                ...state,
				isLoggedIn: action.payload
			}
        break;
        
        case USER_INFO: 
            return {
                ...state,
                user: action.payload
            }

        case MODIFY_USER: 
            return {
                ...state,
                user: action.payload
            }

		default: 
			return state;
	}
}
