import React, { Component } from 'react'
import { Card, Icon, Input, } from 'react-native-elements'
import { BackHandler } from 'react-native';
import {
  Image,
  ImageBackground,
  Linking,
  ListView,
  Platform,
  ScrollView,
  StyleSheet,
  Text,
  View,
  Slider,
  Switch,
  ActivityIndicator
} from 'react-native'
import PropTypes from 'prop-types'
import { FlatList, TouchableOpacity } from 'react-native-gesture-handler'
import { connect } from 'react-redux'
import { deleteContact, modifyUserInfo, setIndexSelected, setSecondInfo } from '../../Action';
import { Avatar } from 'react-native-elements';
import TopMenu from "../../component/Menu/TopMenu"
import * as firebase from 'firebase';
import firestore from 'firebase/firestore'
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import { faEdit } from '@fortawesome/free-solid-svg-icons'
import * as Permissions from 'expo-permissions'
import * as ImagePicker from 'expo-image-picker'
import RadioForm, { RadioButton, RadioButtonInput, RadioButtonLabel } from 'react-native-simple-radio-button';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from 'react-native-responsive-screen'
import axios from "axios";
import Bdd from "../../API/Bdd"
import RNPickerSelect from 'react-native-picker-select';
const DEFAUTL_USER = "https://www.nehome-groupe.fr/wp-content/uploads/2015/09/image-de-profil-2.jpg"
class MySecondSante extends Component {

  constructor(props) {
    super(props)
    this.state = {
      isSelectedProfile: false,
      // id: this.props.second.second_users[this.props.second.indexSelected].idSecondUser,
      id: this.props.navigation.state.params.id,

      blood: "",
      size: 0,
      weight: 0,
      donate: false,
      secu: null,
      idFiche: null,


      photoUri: this.props.second.second_users[this.props.second.indexSelected].imageSecondUser,

      medecin: "",
      allergies: "",
      traitement: "",

      isMedicinExist: false,
      isAllergieExist: false,
      isTraitementExist: false,

      isLoading: false,

      isModifbegin: false,
      isFirst: false,
      ficheSante: {},
    }
    this.ref = firebase.firestore().collection('profile');
    this.handleBackButtonClick = this.handleBackButtonClick.bind(this);
  }

  componentDidMount = async () => {

    this.setState({ isLoading: true })
    await this.fetchSante()
    this.setState({ isLoading: false })
    if (this.props.navigation.state.params.profil !== undefined) {
      this.setState({photoUri: this.props.navigation.state.params.profil})
    }
  }

  componentWillMount() {
    BackHandler.addEventListener('hardwareBackPress', this.handleBackButtonClick);
}

componentWillUnmount() {
    BackHandler.removeEventListener('hardwareBackPress', this.handleBackButtonClick);
}

handleBackButtonClick() {
  console.log('Go to switch sante avec', this.state.photoUri)
    this.props.navigation.navigate("SwitchSecond", {profil: this.state.photoUri})
    return true;
}


  fetchSante = async () => {
    console.log('fetchsante de ', this.state.id)
    await axios.get(`${Bdd.api_url_second}/fiche-sante/list?idSecondUser=${this.state.id}`)
      .then(async res => {
        if (await !res) {
          console.log("tena misy olana")
        } else {
          const fiche = res.data.data
          console.log('resultat de fiche sante ', fiche)
          if (res.data === null) this.setState({ isFirst: true })
          else this.setState({
            isFirst: false,
            blood: fiche.groupeSanguin, size: fiche.taille,
            weight: fiche.poids, medecin: fiche.medecinTraitant,
            secu: fiche.numSecu, donate: fiche.donnateur,
            idFiche: fiche.idFiche, allergies: fiche.allergies
          })
        }
      })
      .catch(err => {
        console.log(err)
      })
  }

  getCameraPermissions = async () => {

    let { status } = await Permissions.askAsync(Permissions.CAMERA_ROLL);
    if (status !== 'granted') {
      Alert("permission not granteed")
      this.setState({
        rollGranted: false,
        cameraGranted: false,
      })
    } else {
      this.setState({
        rollGranted: true,
        cameraGranted: true,
      })
    }
  }

  _pickImage = async () => {
    //launchCameraAsync
    let result = await ImagePicker.launchImageLibraryAsync({
      allowsEditing : true,
      base64: true,
      quality : 0.5
    });

    if (result.cancelled) {
      return
    }


    this._handleImagePicked(result);

  }

  _handleImagePicked = async pickerResult => {
    let uploadUrl =""
    try {
      this.setState({ isLoading: true });

      if (!pickerResult.cancelled) {
        uploadUrl = await this.uploadImageAsync(pickerResult.uri);
      let data = {
        imageSecondUser :uploadUrl,
        idSecondUser : this.state.id
      }
      
         axios.put(`${Bdd.api_url_second}/${this.state.id}/image`,data)
         .then(res=>{
           console.log("vita upload mys maj")
         })

        
      }
    } catch (e) {
      console.log(e);
      alert('Upload failed, sorry :(');
    } finally {
      console.log('finally')
      this.setState({ isLoading: false });
      /* this.props.navigation.navigate("MonProfil") */
      this.setState({photoUri : uploadUrl})
    }
  }
  
  uploadImageAsync = async (uri)  =>{
    // Why are we using XMLHttpRequest? See:
    // https://github.com/expo/expo/issues/2402#issuecomment-443726662
    const blob = await new Promise((resolve, reject) => {
      const xhr = new XMLHttpRequest();
      xhr.onload = function() {
        resolve(xhr.response);
      };
      xhr.onerror = function(e) {
        console.log(e);
        reject(new TypeError('Network request failed'));
      };
      xhr.responseType = 'blob';
      xhr.open('GET', uri, true);
      xhr.send(null);
    });
    const id = Math.random().toString()
    const ref = firebase
      .storage()
      .ref()
      .child(id);
    const snapshot = await ref.put(blob);
  
    // We're done with the blob, close and release it
    blob.close();
  
    return await snapshot.ref.getDownloadURL();
  }




  renderHeader = () => {

    return (
      <View style={styles.headerContainer}>
        <View style={Platform.OS === 'ios' ? styles.under_ios : styles.under}>
          <TopMenu navigation={this.props.navigation} />
        </View>

        <ImageBackground
          style={styles.headerBackgroundImage}
          blurRadius={10}
          source={{ uri: this.state.photoUri == "" ? DEFAUTL_USER : this.state.photoUri }}
        >
          <View style={styles.headerColumn}>

            <Image
              style={styles.userImage}
              source={{ uri: this.state.photoUri == "" ? DEFAUTL_USER : this.state.photoUri }}
            />
            <Text style={styles.userNameText}>{this.props.second.second_users[this.props.second.indexSelected].nomSecondUser} {this.props.second.second_users[this.props.second.indexSelected].prenomSecondUser} </Text>
            <View style={styles.userAddressRow}>
              
              {this.state.isModifbegin === false &&
                <View style={styles.actionBtn}>
                  <TouchableOpacity onPress={() => this.setState({ isModifbegin: !this.state.isModifbegin })}>
                    <Avatar size="medium" rounded overlayContainerStyle={{ backgroundColor: "#008AC8" }} icon={{ name: 'pencil', type: 'font-awesome' }} />
                  </TouchableOpacity>
                </View>
              }
              {this.state.isModifbegin === true &&
                <View style={styles.actionBtn}>
                  <TouchableOpacity onPress={async () => {
                    if (this.state.idFiche === null) {
                      console.log('New fiche sante')
                      await this.addFiche()
                    } else {
                      console.log('Modif fiche sante')
                      await this.modifFiche()
                    }
                    // await this.addFiche()
                    /* if (this.props.second.indexSelected === 0) {
                      await this.modifInfoPerso()

                    } else {
                      await this.modifySecondInfoPerso()

                    } */
                  }}>
                    <Avatar size="medium" rounded overlayContainerStyle={{ backgroundColor: "#008AC8" }} icon={{ name: 'save', type: 'font-awesome' }} />
                  </TouchableOpacity>
                </View>
              }
              <View style={styles.actionBtn}>
                <TouchableOpacity onPress={() => this._pickImage()}>
                  <Avatar size="medium" rounded overlayContainerStyle={{ backgroundColor: "#008AC8" }} icon={{ name: 'camera', type: 'font-awesome' }} />
                </TouchableOpacity>
              </View>
            </View>
          </View>
        </ImageBackground>
      </View>
    )
  }

  addFiche = async () => {
    //this.setState({ isLoading: true })
    let data = {
      // idUser: this.props.user.user.idUser,
      idSecondUser: this.state.id,
      groupeSanguin: this.state.blood,
      taille: this.state.size,
      poids: this.state.weight,
      donnateur: this.state.donate,
      numSecu: this.state.secu,
      medecinTraitant: this.state.medecin,
      allergies : this.state.allergies
    }
    console.log('add fiche sante')
    console.log(data)

    await axios.post(`${Bdd.api_url_second}/fiche-sante`, data)
      .then( res => {
        console.log('resultat add fiche sante ', res)
        this.setState({ isModifbegin: false, isLoading: false })
      })
      .catch(err => console.log(err))
   // this.setState({ isLoading: false })
  }


  modifFiche() {
    this.setState({ isLoading: true })
    let userModified = {

      groupeSanguin: this.state.blood,
      taille: this.state.size,
      poids: this.state.weight,
      donnateur: this.state.donate,
      numSecu: this.state.secu,
      medecinTraitant: this.state.medecin,
      allergies : this.state.allergies
    }

    userModified.groupeSanguin = this.state.blood
    userModified.taille = this.state.size
    userModified.poids = this.state.weight
    userModified.numSecu = this.state.secu
    userModified.donnateur = this.state.donate
    userModified.allergies = this.state.allergies

    console.log('Modif fiche sante', this.state.idFiche)
    console.log(userModified)
    // this.saveAutre(this.state.medecin, this.state.allergie, this.state.traitement)
    axios.put(`${Bdd.api_url_second}/fiche-sante/${this.state.idFiche}`, userModified)
      .then(res => {
        console.log('resultat modif fiche', res)
        this.setState({ isLoading: false, isModifbegin: false })
      })
      .catch(err => console.log(err))
    this.setState({ isLoading: false, isModifbegin: false })
  }

  createTaille = () => {
    taille = []
    for (i = 50; i <= 210; i++ ) {
      const item = {
        label: i + " cm",
        value: i
      }
      taille.push(item)
    }

    return taille
  }

  createPoids = () => {
    poids = []

    for (i = 1; i <= 180; i++ ) {
      const item = {
        label: i + " kg",
        value: i
      }
      poids.push(item)
    }
    return poids
  }


  render() {
    var groupeSanguin = [
      { label: 'A+', value: 0 },
      { label: 'A-', value: 1 },
      { label: 'B+', value: 2 },
      { label: 'B-', value: 3 },
      { label: 'AB+', value: 4 },
      { label: 'AB-', value: 5 },
      { label: 'O+', value: 6 },
      { label: 'O-', value: 7 }
    ];
    var tailleCm = this.createTaille()
    var poidsKg = this.createPoids()
    return (
      <ScrollView style={styles.scroll}>
        <View style={styles.container}>

   

          {this.state.isLoading && <View style={styles.loading_container}>
            <ActivityIndicator size="large" />
          </View>}

          <Card containerStyle={styles.cardContainer}>
            {this.renderHeader()}
          </Card>


          <View style={styles.containProfilSecond}>
          <Text style={styles.profilSecondTxt}>Profil Secondaire</Text>
          </View>
          <View style={styles.infoContainer}>


            <View>
              <Text style={styles.labelText}>Groupe Sanguin</Text>
              {this.state.isModifbegin === true && <RadioForm
                formHorizontal={true}
                labelHorizontal={false}
                radio_props={groupeSanguin}
                initial={-1}
                onPress={(value) => { this.setState({ blood: groupeSanguin[value].label }) }}
              />

              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.blood}</Text>
              }
            </View>
            <View>
              <Text style={styles.labelText}>Taille</Text>
              {this.state.isModifbegin === true && <View>

                <RNPickerSelect
                  placeholder={{
                    label: "Taille en cm"
                  }}
                  onValueChange={(value) => this.setState({ size: value })}
                  items={tailleCm}
                />

              </View>

              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.size} cm</Text>
              }
            </View>




            <View>
              <Text style={styles.labelText}>Poids</Text>
              {this.state.isModifbegin === true && <View>
                <RNPickerSelect
                  placeholder={{
                    label: "Poids en kg"
                  }}
                  onValueChange={(value) => this.setState({ weight: value })}
                  items={poidsKg}
                />


              </View>
              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.weight} kg</Text>
              }
            </View>
            <View>
              <Text style={styles.labelText}>Donneur d'organe</Text>
              {this.state.isModifbegin === true && <Switch
                trackColor={{ false: "#767577", true: "#81b0ff" }}
                thumbColor={this.state.donate ? "#f5dd4b" : "#f4f3f4"}
                ios_backgroundColor="#3e3e3e"
                onValueChange={(value) => {
                  if (value === true) this.setState({ donate: true })
                  else this.setState({ donate: false })
                }}
                value={this.state.donate}
                style={styles.switch}
              />
              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.donate === true ? "OUI" : "NON"}</Text>
              }
            </View>
            <View>
              <Text style={styles.labelText}>Numro de la scurit sociale</Text>
              {this.state.isModifbegin === true && <Input
                keyboardType={"numeric"}
                onChangeText={(text) => this.setState({ secu: text })}
                value={this.state.secu}
              />
              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.secu}</Text>
              }
            </View>



            <View>
              <Text style={styles.labelText}>Mdecin traitant</Text>
              {this.state.isModifbegin === true && <Input

                onChangeText={(text) => this.setState({ medecin: text })}
                value={this.state.medecin}
              />
              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.medecin}</Text>
              }
            </View>

            <View>
              <Text style={styles.labelText}>Allergies</Text>
              {this.state.isModifbegin === true && <Input

                onChangeText={(text) => this.setState({ allergies: text })}
                value={this.state.allergie}
              />
              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.allergies}</Text>
              }
            </View>


          </View>



        </View>
      </ScrollView>
    )
  }
}
