class Test extends Component {
  constructor() {
    super()
    this.state = {
      currentPage: 0,
      typeDoc: "",
      isAutre: false,
      modalVisible: false,

      rollGranted: true,
      cameraGranted: false,
      uri_pict: null,
      pict_name: null,
      uri_doc: null,
      isCamera: false,
      base64Img: null,
      doc: {},
      name: "",
      isLoading: false,
      docCategorie: ""
    }
  }

  componentDidMount = async () => {
    await this.getCameraPermissions()
  }

  getCameraPermissions = async () => {

    let { status } = await Permissions.askAsync(Permissions.CAMERA_ROLL);
    if (status !== 'granted') {
      Alert("permission not granteed")
      this.setState({
        rollGranted: false,
        cameraGranted: false,
      })
    } else {
      this.setState({
        rollGranted: true,
        cameraGranted: true,
      })
    }
  }

  UNSAFE__componentWillReceiveProps(nextProps, nextState) {
    if (nextState.currentPage != this.state.currentPage) {
      if (this.viewPager) {
        this.viewPager.setPage(nextState.currentPage)
      }
    }
  }

  goToDocumentMenu = () => {
    this.props.navigation.navigate("FileManager")
  }

  _pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      allowsEditing : true,
      mediaTypes: ImagePicker.MediaTypeOptions.All,
    });

    //console.log(result)

    this._handleImagePicked(result);

  }

  takePictureAndCreateAlbum = async () => {
    
    let result = await ImagePicker.launchCameraAsync({
      allowsEditing : true,
      quality : 0.5
    });
    console.log("ok")

    this._handleImagePicked(result)
   
  }

  _handleImagePicked = async pickerResult => {
    let uploadUrl =""
    try {
      this.setState({ isLoading: true });

      if (!pickerResult.cancelled) {
        uploadUrl = await this.uploadImageAsync(pickerResult.uri);
        this.setState({uri_doc : uploadUrl})

     
        
      }
    } catch (e) {
      console.log(e);
      alert('Upload failed, sorry :(');
    } finally {
      this.setState({ isLoading: false });
      /* this.props.navigation.navigate("MonProfil") */
      this.setState({photoUri : uploadUrl})
    }
  }
  
  uploadImageAsync = async (uri)  =>{
    // Why are we using XMLHttpRequest? See:
    // https://github.com/expo/expo/issues/2402#issuecomment-443726662
    const blob = await new Promise((resolve, reject) => {
      const xhr = new XMLHttpRequest();
      xhr.onload = function() {
        resolve(xhr.response);
      };
      xhr.onerror = function(e) {
        console.log(e);
        reject(new TypeError('Network request failed'));
      };
     
   xhr.responseType = 'blob';
      xhr.open('GET', uri, true);
      xhr.send(null);
    });
    const id = Math.random().toString()
    const ref = firebase
      .storage()
      .ref()
      .child(id);
    const snapshot = await ref.put(blob);
  
    // We're done with the blob, close and release it
    blob.close();
  
    return await snapshot.ref.getDownloadURL();
  }


 async saveDoc(){
   console.log("doc savena eto")
    let data = {
    
      idUser : this.props.user.user.idUser,
