      {this.state.uri_doc !== null && <Text style={styles.docText}>Votre document est sélectionné</Text>}
      {this.state.uri_pict !== null && <Text style={styles.docText}>Votre photo est sélectionné</Text>}
        
      {this.state.uri_doc !== null && 

