import React, { Component } from 'react'
import { Text, View, StyleSheet, TextInput, TouchableOpacity, Modal } from 'react-native'
import TopMenu from "../../component/Menu/TopMenu"
import { connect } from 'react-redux'
import {
    widthPercentageToDP as wp,
    heightPercentageToDP as hp,
} from 'react-native-responsive-screen';
import { SearchBar, Icon } from 'react-native-elements';
import { ScrollView } from 'react-native-gesture-handler';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import { faHome, faBars, faTimes, faCaretDown, faNotesMedical, faHandsHelping, faPumpMedical, faDisease, faFileImage, faShareAlt, faEye, faTrash } from '@fortawesome/free-solid-svg-icons';
import { Row, Grid } from 'react-native-easy-grid';
import axios from 'axios';
import Bdd from "../../API/Bdd"
import * as Sharing from 'expo-sharing';
import * as  FileSystem from 'expo-file-system';
import BottomMenu from "../../component/Menu/BottomMenu"
import { Avatar, Divider, ListItem } from 'react-native-elements';
class FileManager extends Component {

    constructor(props) {
        super(props)
        this.state = {
            documents: [],
            selectedCate: "all",
            numColored: 0,
            listOption: false,
            selectedDoc: null,
            modalVisible: false
        }
    }

    selectedDocItem() {
        let oneDoc = []
        if (this.state.selectedDoc !== null) {
            oneDoc = this.state.documents.filter(el => {
                return el.id === this.state.selectedDoc
            })
        }
        return oneDoc[0]
    }


    componentDidMount() {
        this._initDoc()
    }
    _initDoc = () => {
        let id = this.props.second.second_users[this.props.second.indexSelected].id;
        let data = {
            user_id: id
        }

        axios.post(Bdd.api_url + '/api/document/one', data)
            .then((response) => {

                if (this.state.selectedCate === "all") {
                    this.setState({ documents: response.data.documents })
                } else
                    if (this.state.selectedCate === "aut") {
                        let selectedDoc = response.data.documents.filter(el => {
                            return (el.name.split("__")[1] !== "all" && el.name.split("__")[1] !== "ordo" &&
                                el.name.split("__")[1] !== "cert" && el.name.split("__")[1] !== "atte" &&
                                el.name.split("__")[1] !== "cptr")
                        })
                        this.setState({ documents: selectedDoc })
                    }
                    else {
                        let selectedDoc = response.data.documents.filter(el => {
                            return el.name.split("__")[1] == this.state.selectedCate
                        })
                        this.setState({ documents: selectedDoc }, () => {
                            //.log('state',this.state)
                        })
                    }

            })
    }

    renderCardCate = (text, isColor, cate, index) => (
        <TouchableOpacity key={index} onPress={() => { this.handleCate(cate, index) }} style={[styles.cardContainer, isColor == true ? { backgroundColor: "#008ac2" } : { backgroundColor: "white" }]}>
            <FontAwesomeIcon size={30} style={[styles.iconCard, isColor == true ? { color: "white" } : { color: "grey" }]} icon={faNotesMedical} />
            <Text style={[styles.cardText, isColor == true ? { color: "white" } : { color: "grey" }]}>{text}</Text>
        </TouchableOpacity>

    )
    handleCate = async (cate, index) => {
        await this.setState({
            selectedCate: cate,
            numColored: index
        })

        this._initDoc()
    }
    goToDetail = (id) => {
        this.props.navigation.navigate("DocumentDetail", { id: id })
    }

    renderCardDoc = (id, docName, docDate, index) => (
        <TouchableOpacity onPress={() => this.goToDetail(id)} onLongPress={() => this.perform(id)} key={index} style={{ flexDirection: "column", marginRight: wp("1%") }}>
            <View style={styles.docType}>
                <Icon name="collections" />
            </View>
            <Text style={styles.docName}>{docName}</Text>
            <Text style={styles.docName}>{docDate}</Text>
        </TouchableOpacity>
    )

    blabla = (id, docName, docDate, index) => (
        <ListItem
            key={index}
            containerStyle={{ margin: 10 }}

            title={docName}
            subtitle={docDate}
            leftIcon={<FontAwesomeIcon icon={faFileImage} />}
            bottomDivider
            rightElement={
                <React.Fragment>
                    <TouchableOpacity style={{ padding: 10 }} onPress={() => this.goToDetail(id)}>
                        <FontAwesomeIcon icon={faEye} />
                    </TouchableOpacity>
                    <TouchableOpacity style={{ padding: 10 }} onPress={() => this.shareDoc(id)}>
                        <FontAwesomeIcon icon={faShareAlt} />
                    </TouchableOpacity>
                    <TouchableOpacity style={{ padding: 10 }} onPress={() => this.deleteDoc(id)}>
                        <FontAwesomeIcon icon={faTrash} />
                    </TouchableOpacity>
                </React.Fragment>
            }
        />
    )

    perform = (id) => {
        this.setState({
            selectedDoc: id,
            modalVisible: true
        })
    }
    shareDoc = async (id) => {

        await this.getDoc(id)
        const image_source = Bdd.pict_url + this.state.uri
        FileSystem.downloadAsync(
            image_source,
            FileSystem.documentDirectory + ".jpg"
        )
            .then(({ uri }) => {
                console.log('Finished downloading to ', uri);

                Sharing.shareAsync(uri);
            })



    };
    getDoc = async (id) => {
        let data = {
            id: id
        }
        axios.post(Bdd.api_url + "/api/document/detail", data)
            .then((response) => {
                this.setState({
                    name_doc: response.data.document.nam,
                    uri: response.data.document.url
                })
            })
    }
    deleteDoc(id) {
        let data = {
            id: id,
            upload_preset: 'aoknragz'
        }


        axios.post(Bdd.api_url + '/api/document/delete', data)
            .then((response) => {
                //.log('res document',response);
                if (response.data.status === 200) {
                    this._initDoc()
                }
            })
    }
    render() {
        const datas = [
            {
                type: "Tous", label: "all"
            }, {
                type: "Mes ordonnances", label: "ordo"
            }, {
                type: "Mes certificats", label: "cart"
            }, {
                type: "Mes attestations", label: "atte"
            }, {
                type: "Mes comptes-rendus", label: "cptr"
            }, {
                type: "Autres", label: "aut"
            }
        ]
        return (
            <View style={{ flex: 1 }}>
                <ScrollView style={styles.container}>
                    <View style={Platform.OS === 'ios' ? styles.under_ios : styles.under}>
                        <TopMenu navigation={this.props.navigation} />
                    </View>

                    <Modal
                        animationType="none"
                        transparent={true}
                        visible={this.state.modalVisible}

                    >
                        <View style={styles.centeredView}>
                            <View style={styles.modalView}>
                                {this.state.selectedDoc !== null && this.selectedDocItem() !== undefined &&
                                    <Text style={{ color: "grey", marginBottom: 5 }}>{this.selectedDocItem().name.split("__")[0]}</Text>
                                }

                                <Text style={{ fontSize: 26, marginBottom: 5 }}>Choisir une action</Text>
                                <View style={[styles.actionButton, { marginBottom: 7 }]}>

                                    <TouchableOpacity style={styles.btnAction} onPress={() => this.shareDoc()}>
                                        <Icon name="share" />
                                    </TouchableOpacity>

                                    <TouchableOpacity style={styles.btnAction}
                                        onPress={() => {
                                            this.state.selectedDoc !== null ? this.deleteDoc() : ""
                                        }} >
                                        <Icon name="trash" />
                                    </TouchableOpacity>


                                </View>
                                <TouchableOpacity style={{ backgroundColor: "#d3d3d3", padding: 5 }} onPress={() => this.setState({ modalVisible: false })}>
                                    <Text style={{ color: "white", textDecorationLine: "underline" }}>Annuler</Text>
                                </TouchableOpacity>
                            </View>
                        </View>
                    </Modal>

                    <View style={styles.categories}>
                        <ScrollView indicatorStyle="white" nestedScrollEnabled={true} horizontal={true}>
                            {datas.map((data, i) => {
                                return this.state.numColored === i ? this.renderCardCate(data.type, true, data.label, i) : this.renderCardCate(data.type, false, data.label, i)
                            })}
                        </ScrollView>
                    </View>

                    <View style={styles.docContainer}>

                        <View style={styles.actionButton}>
                            <TouchableOpacity
                                style={{ marign: 5, padding: 10, backgroundColor: "#008ac2", }}
                                onPress={() => this.props.navigation.navigate("Test")}>
                                <Text style={{ color: "white", textAlign: "center" }}>Ajouter un document</Text>
                            </TouchableOpacity>

                        </View>
                        <View >
                            <ScrollView>
                                {this.state.documents.length !== 0 && this.state.documents.map((document, i) => {
                                    let name = document.name.split("__")[0]
                                    let date = document.name.split("__")[2]
                                    return this.blabla(document.id, name, date, i)
                                })}
                            </ScrollView>

                        </View>

                    </View>

                </ScrollView>

                <BottomMenu navigation={this.props.navigation} />

            </View>

        )
    }
}
