import React, { Component } from 'react'

import { CheckBox, Text, View, StyleSheet, ScrollView, TouchableOpacity, Image, TextInput } from 'react-native'
import TopMenu from "../../component/Menu/TopMenu"
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from 'react-native-responsive-screen';
import { connect } from 'react-redux';
import { addContact } from "../../Action"
import { Card, Icon, Input } from 'react-native-elements'
class NewContact extends Component {

  constructor() {
    super()
    this.state = {
      error: null,

    }

    this.firstName = "";
    this.lastName = "";
    this.email = "";
    this.phone = "";
  }
  onChangeInput(text, type) {
    this[type] = text;
  }

  onClickaddContact() {
    let contact = {
      firstName: this.firstName,
      lastName: this.lastName,
      email: this.email,
      phone: this.phone,
      user_id: this.props.user.user.data.user[0].id
    }
    //.log(contact);
    this.props.addContact(contact);
    this.gotToProfil();
  }


  gotToProfil() {
    this.props.navigation.push("MyContact")
  }

  render() {
    return (
      <View style={styles.container}>
        <View style={Platform.OS === 'ios' ? styles.under_ios : styles.under}>
          <TopMenu navigation={this.props.navigation} />
        </View>

        <Text style={{ fontWeight: "bold", fontSize: 20, textAlign: "center" }}>Ajouter un contact d'urgence</Text>


        {this.state.error !== null && <Text style={styles.center, styles.red}>{this.state.error}</Text>}
        <View style={{margin : 20}}>
          <Input

            onChangeText={(text) => this.firstName = text}

            placeholder="Prénom"
          //onSubmitEditing={() => this._searchFilms()}
          />
          <Input

            onChangeText={(text) => this.lastName = text}

            placeholder="Nom"
          //onSubmitEditing={() => this._searchFilms()}
          />
          <Input

            autoCapitalize="none"
            onChangeText={(text) => this.email = text}

            placeholder="Adresse email"
          //onSubmitEditing={() => this._searchFilms()}
          />

          <Input

            keyboardType={"numeric"}
            onChangeText={(text) => this.phone = text}

            placeholder="Téléphone"
          //onSubmitEditing={() => this._searchFilms()}
          />



          <TouchableOpacity
            style={styles.buttonStyle}
            onPress={(e) => { this.onClickaddContact() }}
          >
            <Text style={styles.buttonText}>Enregistrer le contact </Text>
          </TouchableOpacity>
          <Image
            style={{ width: wp('13%'), height: wp('13%'), position: "relative", bottom: hp('12.5%'), left: wp('59%') }}
            source={require('../../images/icon_urf_bleu.png')}
          />

        </View>


      </View>
    )
  }
}



const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: 'white',


  },
  container2: {
    flex: 1,
    justifyContent: "center",
    alignItems: "center",

    alignItems: 'center',
    paddingTop: hp('8%'),

    zIndex: 2
  },
  header: {
    position: 'relative',
    top: hp("20%"),
    zIndex: 4,
    textAlign: 'center'
  },
  center: {
    textAlign: 'center',
    color: "white",
    fontSize: 23,
    marginTop: hp('1%'),
    marginBottom: hp('1%'),
    fontWeight: 'bold'
  },
  red: {
    color: 'red'
  },
  body: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  textinput: {
    marginLeft: 5,
    marginRight: 5,
    height: 50,
    width: wp("70%"),
    paddingLeft: 15,
    backgroundColor: 'white',
    borderColor: "#000",
    borderRadius: 3,
    marginBottom: hp('2%')
  },
  footer: {
    position: 'relative',
    bottom: hp("10%"),
    zIndex: 4,
    textAlign: 'center',
    textDecorationLine: 'underline',
    paddingTop: 15,
    paddingBottom: 15,
    paddingLeft: 15,
    paddingRight: 15,
    color: "white"
  },
  title2: {
    color: "white",
    marginBottom: hp('1%'),
    marginTop: hp('1%'),
    textAlign: "center"
  },
  buttonStyle: {
    backgroundColor: "#008ac8",
    marginTop: hp("2%"),
    marginBottom: hp("2.5%"),
    paddingTop: hp('3%'),
    paddingBottom: hp('3%'),
    paddingLeft: wp("2%"),
    paddingRight: wp("2%")
    /*  marginTop: hp("3%"), 
     width: wp("60%"), 
     marginLeft: wp('5%'),
     borderColor:'red',
     backgroundColor: "#008AC8",
     paddingTop: hp('3%'),
     paddingBottom: hp('3%'),
     marginBottom: hp("5%") */
  },
  buttonText: {
    color: 'white',
    textAlign: "center",
    fontSize: 13
  },

})

const mapStateToProps = (store) => {
  return {
    user: store.user,
    contact: store.contact
  }
}

const mapDispatchToProps = {
  addContact
}
export default connect(mapStateToProps, mapDispatchToProps)(NewContact)
