import React from 'react';
import { StyleSheet, Text, View, AsyncStorage, ScrollView, TouchableOpacity, Image, ActivityIndicator } from 'react-native';
import { Button } from 'react-native-elements';
import { withNavigation } from 'react-navigation';
import { connect } from 'react-redux';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from 'react-native-responsive-screen';
import Footer from "./Menu/Footer";
import InputSecondProfil from './InputSecondProfil';
import { setSecondInfo } from '../Action';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import { faPen, faToilet } from '@fortawesome/free-solid-svg-icons'
import TopMenu from "../component/Menu/TopMenu"
import axios from 'axios';
import Bdd from '../API/Bdd'
import { Card, Icon, Input } from 'react-native-elements'
import RadioForm, { RadioButton, RadioButtonInput, RadioButtonLabel } from 'react-native-simple-radio-button';

class SecondAdd extends React.Component {

  constructor(props) {
    super(props);
    this.firstName = "",
      this.lastName = "",
      this.address = "",
      this.zip = "",
      this.city = "",
      this.phone = "",
      this.blood = "",
      this.size = "",
      this.weight = "",
      this.donate = "",
      this.secu = "",
      this.lien = "",

      this.state = {
        errFirstName: null,
        errLastName: null,
        errZip: null,
        errVille: null,
        errAddress: null,
        errPhone: null
      }
  }

  componentDidMount() {

  }



  goToContact = () => {

    this.props.navigation.push("Contact")
  }


  async saveSecondProfil() {

    let data = {
      firstName: this.firstName + "__" + this.lien,
      lastName: this.lastName,
      address: this.address,
      zip: this.zip,
      city: this.city,
      phone: this.phone,
      blood: this.blood,
      size: this.size,
      weight: this.weight,
      donate: this.donate,
      secu: this.secu,
      user_id: this.props.user.user.data.user[0].id
    }

   // let errorCount = await this.validationInfo(data)

      let errorCount = this.validateData(data);
      if (errorCount > 0) {
        return false;
      }

      axios.post(Bdd.api_url + '/api/second_user/add', data)
        .then((response) => {

          if (response.data.status === 200) {

            AsyncStorage.getItem('bosToken')
              .then((token) => {
                let user_id = this.props.user.user.data.user[0].id
                this.props.setSecondInfo(token, user_id)
                
                this.props.navigation.navigate("MyContact")
              })


          }

        })
    

  }

  validateData(data) {
    let count = 0
    var alphaPattern = /^([a-zA-Z0-9 _-]+)$/

    this.setState({errFirstName: null}, {errLastName: null}, {errVille: null}, 
      {errAddress: null}, {errZip: null}, {errPhone: null})

    if (data.firstName == "") {
      count++
      this.setState({ errFirstName: "Prénom obligatoire" })
    } else if (alplaPattern.test(data.firstName) === false) {
      count++
      this.setState({ errFirstName: "caractères alphanumerique" })
    }

    if (data.lastName == "") {
      count++
      this.setState({ errLastName: "Nom obligatoire" })
    } else if (alphaPattern.test(data.lastName) === false) {
      count++
      this.setState({ errLastName: "caractères alphanumerique" })
    }

    if (data.city == "") {
      count++
      this.setState({ errVille: "Ville obligatoire" })
    } else if (alphaPattern.test(data.lastName) === false) {
      count++
      this.setState({ errVille: "caractères alphanumerique" })
    }

    if (data.address == "") {
      count++
      this.setState({ errAddress: "Adresse obligatoire" })
    } else if (alphaPattern.test(data.address) === false) {
      count++
      this.setState({ errAddress: "caractères alphanumerique" })
    }

    if (data.zip == "") {
      count++
      this.setState({ errZip: "Code postal obligatoire" })
    } else if (alphaPattern.test(data.address) === false) {
      count++
      this.setState({ errZip: "caractères alphanumerique" })
    }

    if (data.phone == "") {
      count++
      this.setState({ errPhone: "Téléphone obligatoire" })
    } else if (data.phone.length <8) {
      count++
      this.setState({ errPhone: "Numéro entre 8 à 12 chiffres" })
    }

    return count;
  }


  async validationInfo() {
    var numberPattern = /^[0-9]+$/
    var alphaPattern = /^([a-zA-Z0-9 _-]+)$/
    let count = 0

    /* if (data.phone.length < 8 || data.phone.length > 12 || isNaN(data.phone)) {
      count++
      this.setState({ errPhone: "Vous devez entrer un numéro de téléphone valide" })
    } else {
      this.setState({ errPhone: null })
    } */

    if (alplaPattern.test(data.firstName) === false || data.firstName == "") {
      count++
      this.setState({ errFirstName: "Champs prénom obligatoire" })
    } else {
      this.setState({ errFirstName: null })
    }
    if (alplaPattern.test(data.lastName) === false || data.lastName == "") {
      count++
      this.setState({ errLastName: "Champs prénom obligatoire" })
    } else {
      this.setState({ errLastName: null })
    }

    if (numberPattern.test(data.zip) === false || this.state.zip == "" || this.state.zip.length < 3 || this.state.zip.length > 6) {
      count++
      this.setState({ errZip: "Vous devez entrer un code postal valide" })
    } else {
      this.setState({ errZip: null })
    }

    if (alphaPattern.test(data.address) === false || this.state.address == "") {
      count++
      this.setState({ errAddress: "Vous devez entrer une adresse valide" })
    } else {
      this.setState({ errAddress: null })
    }
    if (alphaPattern.test(data.city) === false || this.state.city == "") {
      count++
      this.setState({ errVille: "Vous devez entrer un nom de ville valide" })
    } else {
      this.setState({ errVille: null })
    }


  }

  render() {
    var lienProfil = [
      { label: 'père', value: 0 },
      { label: 'mère', value: 1 },
      { label: 'fils', value: 2 },
      { label: 'fille', value: 3 },
      { label: 'autre asc', value: 4 },
      { label: 'autre desc', value: 5 },
    ];
    return (
      <View style={styles.container}>
        {this.state.isLoading && <View style={styles.loading_container}>
          <ActivityIndicator size="large" />
        </View>}

        <View style={Platform.OS === 'ios' ? styles.under_ios : styles.under}>
          <TopMenu navigation={this.props.navigation} />
        </View>

        <View style={styles.main}>


          <Text style={styles.title2}>Création d'un profil secondaire</Text>

          <Input
            placeholder="Prénom"
            onChangeText={(text) => { this.firstName = text }}
            containerStyle={styles.inputVaovao}
          />
          <Text style={styles.invalid}>{this.state.errFirstName}</Text>

          <Input
            placeholder="Nom"
            onChangeText={(text) => { this.lastName = text }}
            containerStyle={styles.inputVaovao}
          />
          <Text style={styles.invalid}>{this.state.errLastName}</Text>

          <Input
            placeholder="Ville"
            onChangeText={(text) => { this.city = text }}
            containerStyle={styles.inputVaovao}
          />
          <Text style={styles.invalid}>{this.state.errVille}</Text>

          <Input
            placeholder="Adresse"
            onChangeText={(text) => { this.address = text }}
            containerStyle={styles.inputVaovao}
          />
          <Text style={styles.invalid}>{this.state.errAddress}</Text>

          <Input
            placeholder="Code postale"
            onChangeText={(text) => { this.zip = text }}
            containerStyle={styles.inputVaovao}
            keyboardType={"numeric"}
          />
          <Text style={styles.invalid}>{this.state.errZip}</Text>

          <Input
            placeholder="Téléphone"
            onChangeText={(text) => { this.phone = text }}
            containerStyle={styles.inputVaovao}
            keyboardType={"numeric"}
            maxLength={12}
          />
          <Text style={styles.invalid}>{this.state.errPhone}</Text>

          <RadioForm
            formHorizontal={true}
            labelHorizontal={false}
            radio_props={lienProfil}
            initial={-1}
            onPress={(value) => { this.setState({ lien: lienProfil[value].label }) }}
          />



          <TouchableOpacity
            onPress={(e) => { this.saveSecondProfil() }}
            style={styles.deco}
          >
            <Text style={styles.textItem}>Créer le compte</Text>
          </TouchableOpacity>


        </View>

      </View>
    );
  }
}

