import React from 'react'
import {View,Text,StyleSheet,Image,TextInput,Button, Alert,TouchableOpacity,ScrollView,ActivityIndicator } from 'react-native'
import question from '../Helpers/question'
import { connect } from 'react-redux'
const mapStateToProps = (state) => {
    return {
        Idquestion : state.Idquestion,
        point :state.point,
        age:state.age,
        taille:state.taille,
        poids:state.poids
    }
  }
  const mapDispatchToProps = (dispatch) => {
    return {
      dispatch: (action) => { dispatch(action) }
    }
  }
  
class AutoDiagnostique extends React.Component{
    constructor(props){
        super(props) 
        this.state = {
            index:1,
            error: null,
            isLoading: false,
            text:"0"
          }  
          this.age=28
          this.taille=175
          this.poid=70 
          this.textInput="";   
    }

  
    _displayLoading() {
        if (this.state.isLoading) {
          return (
            <View style={styles.loading_container}>
              <ActivityIndicator size="large" color="#008ac8" />
            </View>
          )
        }
      }
      displayHoldPlacer(){
          if(this.qs.id===2)
          {
            return "votre temperature"
          }
          if(this.qs.id===11)
          {
            return "votre âge"
          }
          if(this.qs.id===12)
          {
            return "en cm"
          }
          if(this.qs.id===13)
          {
            return "en kg"
          }
      }
    nexIndex(p){
        this.setState({ isLoading: true })
        const index=this.qs.id+1;
        const point=this.props.Idquestion.point+p;
