firebase.initializeApp(firebaseConfig); */



class Test extends Component {
  constructor() {
    super()
    this.state = {
      currentPage: 0,
      typeDoc: "",
      isAutre: false,
      modalVisible: false,

      rollGranted: true,
      cameraGranted: false,
      uri_pict: null,
      pict_name: null,
      uri_doc: null,
      isCamera: false,
      base64Img: null,
      doc: {},
      name: "",
      isLoading: false,
      docCategorie: ""
    }
  }

  componentDidMount = async () => {
    await this.getCameraPermissions()
  }

  getCameraPermissions = async () => {

    let { status } = await Permissions.askAsync(Permissions.CAMERA_ROLL);
    if (status !== 'granted') {
      Alert("permission not granteed")
      this.setState({
        rollGranted: false,
        cameraGranted: false,
      })
    } else {
      this.setState({
        rollGranted: true,
        cameraGranted: true,
      })
    }
  }

  UNSAFE__componentWillReceiveProps(nextProps, nextState) {
    if (nextState.currentPage != this.state.currentPage) {
      if (this.viewPager) {
        this.viewPager.setPage(nextState.currentPage)
      }
    }
  }

  goToDocumentMenu = () => {
    this.props.navigation.navigate("FileManager")
  }

  _pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      allowsEditing : true,
      base64: true
    });

    //console.log(result)

    if (!result.cancelled) {
      this.setState({ uri_doc: result.uri, uri_pict: null, base64Img: `data:image/jpg;base64,${result.base64}` })

    }

  }
  saveImg() {
    console.log(this.state.docCategorie)
    this.setState({ isLoading: true });
    let apiUrl = 'https://api.cloudinary.com/v1_1/hb314jso8/image/upload';
    if (this.state.uri_doc !== null) {
      console.log("ato zao")

      let data = {
        "file": this.state.base64Img,
        "upload_preset": 'aoknragz'
      }

      fetch(apiUrl, {
        body: JSON.stringify(data),
        headers: {
          'content-type': 'application/json'
        },
        method: 'POST',
      }).then(async r => {
        let data = await r.json()
        //   console.log(data)

        if (data.error) {
          console.log('pas enregistrÃ©')
          return;
        }

        var today = new Date();
        var dd = String(today.getDate()).padStart(2, '0');
        var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
        var yyyy = today.getFullYear();

        today = dd + '-' + mm + '-' + yyyy;


        let specialTempDocName = this.state.name + "__" + this.state.docCategorie + "__" + today

        let doc = {
          name: specialTempDocName,
          url: '/' + data.public_id + '.' + data.format,
          user_id: this.props.second.second_users[this.props.second.indexSelected].id,
          public_id: data.public_id
        }

          console.log('doc', doc);

        axios.post(Bdd.api_url + '/api/document/add', doc)
          .then((response) => {
            //console.log('res document',response);
            if (response.data.status === 200) {
              this.setState({ isLoading: false })
             
              this.goToDocumentMenu()
            }
          })


        return data.secure_url
      }).catch(err => console.log("misy olana ", err))
    } else if (this.state.uri_pict !== null) {
      let data = {
        "file": this.state.base64Img,
        "upload_preset": 'aoknragz'
      }

      fetch(apiUrl, {
        body: JSON.stringify(data),
        headers: {
          'content-type': 'application/json'
        },
        method: 'POST',
      }).then(async r => {
        let data = await r.json()
        //     console.log('les data photo', data);

        if (data.error) {
          return;
        }
        var today = new Date();
        var dd = String(today.getDate()).padStart(2, '0');
        var mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
        var yyyy = today.getFullYear();

        today = dd + '-' + mm + '-' + yyyy;


        let specialTempDocName = this.state.name + "__" + this.state.docCategorie + "__" + today

        let doc = {
          name: specialTempDocName,
          url: '/' + data.public_id + '.' + data.format,
          user_id: this.props.second.second_users[this.props.second.indexSelected].id,
          public_id: data.public_id
        }

        console.log("doc",doc)

        axios.post(Bdd.api_url + '/api/document/add', doc)
          .then((response) => {
            //  console.log('res document',response);
            if (response.data.status === 200) {

              this.setState({ isLoading: false })
             
              this.goToDocumentMenu()
            }
          })


        return data.secure_url
      }).catch(err => console.log(err))
    } else {
      console.log('rien')
    }

  }
  takePictureAndCreateAlbum = async () => {
    console.log('tpaca');
    await this.setState({ isLoading: true });

    const result = await this.camera.takePictureAsync({
      
      base64: true
    });

    
    let pict_name = result.uri.split('/');
    

    this.setState({ isLoading: false, uri_pict: result.uri, pict_name: pict_name[pict_name.length - 1], base64Img: `data:image/jpg;base64,${result.base64}` }, () => {
      
    })
   
  }
  getCamera() {
    return (
      <View style={styles.container}>
        {this.state.isLoading && <View style={styles.loading_container}>
          <ActivityIndicator size="large" />
        </View>}
        {this.state.uri_pict === null && <Camera
          type={Camera.Constants.Type.back}
          style={{ flex: 1 }}
          ref={ref => {
            this.camera = ref;
          }}
        />}
        {this.state.uri_pict === null && <TouchableOpacity
          onPress={() =>
            this.state.rollGranted && this.state.cameraGranted
              ? this.takePictureAndCreateAlbum()
              : Alert.alert('Permissions not granted')
          }
          style={styles.buttonContainer}>
          <View style={styles.buttonRond}>

          </View>
        </TouchableOpacity>}
       
        {this.state.uri_pict === null && <TouchableOpacity
          onPress={() =>
            this.setState({ isCamera: false })
          }
          style={styles.buttonContainer2}>
          <View style={styles.buttonBack}>
            <Text style={styles.textBack}>
              retour
                  </Text>
          </View>
        </TouchableOpacity>}
        {this.state.uri_pict !== null && <Image
          style={{ marginLeft: wp('0%'), marginTop : hp("20%"),  width: wp('100%'), height: wp('100%') }}
          source={{ uri: this.state.uri_pict }}
        />}
        {this.state.uri_pict !== null && <View>
          {this.state.uri_pict !== null && <TouchableOpacity
          onPress={() =>
            this.setState({ uri_pict: null })
          }
          style={styles.buttonContainer}>
          <View style={styles.button}>
            <Text style={styles.buttonText}>
              Annuler
                  </Text>
          </View>
        </TouchableOpacity>}
        {this.state.uri_pict !== null && <TouchableOpacity
          onPress={() =>
            this.setState({ isCamera: false, uri_doc: null })
          }
          style={styles.buttonContainer3}>
          <View style={styles.button}>
            <Text style={styles.buttonText}>
              Sauvegarder
                  </Text>
          </View>
        </TouchableOpacity>}
      
        </View>}

      </View>
    );
  }

  renderViewPagerPage = data => {
    return (
      <View style={styles.page}>
        <Text>{data}</Text>
      </View>
    )
  }

  renderDocumentType = (typeDocument) => (
    <View style={styles.page}>
      <View>
        <Text style={{fontWeight : "bold", fontSize : 20,paddingBottom : 70}}>Choisir le type de votre document</Text>
      </View>
      <RadioForm
        
        radio_props={typeDocument}
        initial={-1}
        onPress={(value) => {
          if (typeDocument[value].title === "aut") {
            this.setState({  isAutre: true })
          } else {
            this.setState({ docCategorie: typeDocument[value].title, isAutre: false })
          }

        }}
      />
      {this.state.isAutre === true && <TextInput onChangeText={(text) => this.setState({
        docCategorie : text
      })} placeholder="autre doc" />}
      {this.state.docCategorie !== "" &&
      <TouchableOpacity
         style={{ ...styles.openButton, backgroundColor: "#2196F3" }}
      onPress={() => {
        this.viewPager.setPage(2)
      }}>
         <Text style={styles.textStyle}>Suivant</Text> 
        
      </TouchableOpacity>}
    </View>

  )

  renderDocumentUpload = () => (
    <View style={styles.page}>

      <Modal
        
        animationType="slide"
        transparent={true}
        visible={this.state.modalVisible}
        onRequestClose={() => {
          Alert.alert("Modal has been closed.");
        }}
      >
        <View style={styles.centeredView}>
         
          <View style={styles.modalView}>
          <TouchableOpacity ></TouchableOpacity>
            <TouchableHighlight
              style={{ ...styles.openButton, backgroundColor: "#2196F3" }}
              onPress={() => {
                this.setState({ modalVisible: !this.state.modalVisible })
                this._pickImage()
              }}
            >
              <Text style={styles.textStyle}>Importer  </Text>
            </TouchableHighlight>
            <TouchableHighlight
              style={{ ...styles.openButton, backgroundColor: "#2196F3" }}
              onPress={() => {
                this.setState({ modalVisible: !this.state.modalVisible, isCamera : true })
              
              }}
            >
              <Text style={styles.textStyle}>Utiliser camÃ©ra</Text>

            </TouchableHighlight>
            <TouchableHighlight
              style={{ ...styles.openButton, backgroundColor: "#d3d3d3" }}
              onPress={() => {
                this.setState({ modalVisible: !this.state.modalVisible})
              
              }}
            >
              <Text style={styles.textStyle}>annuler</Text>

            </TouchableHighlight>
          </View>
        </View>
      </Modal>

      
        
      <Text style={{fontSize : 20, fontWeight : "bold"}}>Attacher un document</Text>
        
      <TouchableOpacity style={{paddingTop :  70}} onPress={() => this.setState({ modalVisible: true })}>
        <FontAwesomeIcon size={70} icon={faUpload}  />
      </TouchableOpacity  >
    
      
      {this.state.uri_doc !== null && <Text style={styles.docText}>Votre document est sÃ©lectionnÃ©</Text>}
      {this.state.uri_pict !== null && <Text style={styles.docText}>Votre photo est sÃ©lectionnÃ©</Text>}
        
      {this.state.uri_doc !== null && 

       <TouchableOpacity 
       style={{ ...styles.openButton, backgroundColor: "#2196F3" }}
       onPress={() => this.viewPager.setPage(1 )}
        >
           <Text style={styles.textStyle}>Suivant</Text>
          
         
        </TouchableOpacity>} 
        {this.state.uri_pict !== null && 

<TouchableOpacity 
style={{ ...styles.openButton, backgroundColor: "#2196F3" }}
onPress={() => this.viewPager.setPage(1 )}
 >
    <Text style={styles.textStyle}>Suivant</Text>
   
  
 </TouchableOpacity>} 
    </View>
  )

  renderDocumentName = () => (
    <View style={styles.page}>
      <Text style={{fontWeight : "bold",fontSize : 20,paddingBottom : 70}}>Donnez un nom Ã  votre document</Text>
      <TextInput style={{padding : 10, borderColor : "#000"}} onChangeText={(value) => {
        this.setState({ name: value })
      }} placeholder="nom du doc" />
      <TouchableOpacity style={{padding : 10,backgroundColor : "#00C1B4"}} onPress={() => { this.saveImg() }}>
        <Text style={styles.textStyle}>
          Sauvegarder le  document
      </Text>
      </TouchableOpacity>
      
    </View>
  )





  render() {
    var documentType = [
      { label: 'Ordonnance', value: 0, title: "ordo" },
      { label: 'Certificat', value: 1, title: "cert" },
      { label: 'Attestation', value: 2, title: "atte" },
      { label: 'Compte-rendu', value: 3, title: "cptr" },
      { label: 'Autre', value: 4, title: "aut" },
    ];
    return (
      <View style={styles.container}>
        {this.state.isLoading && <View style={styles.loading_container}>
          <ActivityIndicator size="large" />
        </View>}

        {this.state.isCamera === true && this.getCamera()}
        {this.state.isCamera === false &&
          <View style={Platform.OS === 'ios' ? styles.under_ios : styles.under}>
            <TopMenu navigation={this.props.navigation} />
          </View>  }

         {this.state.isCamera === false && 
          <ViewPager
            style={{ flexGrow: 1 }}
            ref={viewPager => {
              this.viewPager = viewPager
            }}
            onPageSelected={page => {
              this.setState({ currentPage: page.position })
            }}
          >
            {this.renderDocumentUpload()}
            {this.renderDocumentType(documentType)}
           
            {this.renderDocumentName()}
          </ViewPager>}
         
      </View>
    )
  }

  onStepPress = position => {
    this.setState({ currentPage: position })
    this.viewPager.setPage(position)
  }



  renderStepIndicator = params => (
    <MaterialIcon {...getStepIndicatorIconConfig(params)} />
  )

  renderLabel = ({ position, stepStatus, label, currentPosition }) => {
    return (
      <Text
        style={
          position === currentPosition
            ? styles.stepLabelSelected
            : styles.stepLabel
        }
      >
        {label}
      </Text>
    )
  }
}

{/* <View style={styles.stepIndicator}>
          <StepIndicator
            customStyles={firstIndicatorStyles}
            currentPosition={this.state.currentPage}
            labels={['Account', 'Profile', 'Band', 'Membership', 'Dashboard']}
            renderLabel={this.renderLabel}
            onPress={this.onStepPress}
          />
