import React, { Component } from 'react'
import { Card, Input, Avatar } from 'react-native-elements'
import {
    BackHandler,
    Image,
    ScrollView,
    StyleSheet,
    Text,
    View,
    Switch,
    ActivityIndicator,
    Alert,
    Share
} from 'react-native'

import { connect } from 'react-redux'
import { deleteContact, modifyUserInfo, setIndexSelected, setSecondInfo } from '../../Action'

// import TopMenu from '../../component/Menu/TopMenu'
import * as firebase from 'firebase'
import * as Permissions from 'expo-permissions'
import * as ImagePicker from 'expo-image-picker'
import * as DocumentPicker from 'expo-document-picker';
import RadioForm from 'react-native-simple-radio-button'
import {
    widthPercentageToDP as wp,
    heightPercentageToDP as hp
} from 'react-native-responsive-screen'
import axios from 'axios'
import Bdd from '../../API/Bdd'
import RNPickerSelect from 'react-native-picker-select'
import { TouchableOpacity } from 'react-native-gesture-handler'
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import { faAngleLeft, faFilePdf, faShareAlt as faShare } from '@fortawesome/free-solid-svg-icons'
const DEFAUTL_USER = 'https://www.nehome-groupe.fr/wp-content/uploads/2015/09/image-de-profil-2.jpg'

class SanteSecond extends Component {
    constructor (props) {
        super(props)
        this.state = {
            isModifbegin: false,
            isSelectedProfile: false,
            id: this.props.navigation.state.params.id,
            uri_pdf: null,
            photo_pdf: 'vide',
            name_pdf: 'Mon Fichier PDF',
            blood: '',
            size: 0,
            weight: 0,
            donate: false,
            secu: null,
            idFiche: null,

            photoUri: this.props.navigation.state.params.profil,

            medecin: '',
            allergies: '',
            traitement: '',

            isMedicinExist: false,
            isAllergieExist: false,
            isTraitementExist: false,

            isLoading: false,

            isFirst: false,
            ficheSante: {},
            bloodAnnex: { label: 'B+', value: 2 }

        }
        this.name_fichier = '';
        this.ref = firebase.firestore().collection('profile')
        this.handleBackButtonClick = this.handleBackButtonClick.bind(this)
    }

    componentDidMount = async () => {
        console.log('Sante Second')
        this.setState({ isLoading: true })
        await this.fetchSante()
        this.setState({ isLoading: false })
        if (this.props.navigation.state.params.profil !== undefined) {
          this.setState({photoUri: this.props.navigation.state.params.profil})
        }
    }

    componentWillMount() {
        BackHandler.addEventListener('hardwareBackPress', this.handleBackButtonClick);
    }
    
    componentWillUnmount() {
        BackHandler.removeEventListener('hardwareBackPress', this.handleBackButtonClick);
    }
    
    handleBackButtonClick() {
      console.log('Go to switch sante avec', this.state.photoUri)
        this.props.navigation.navigate("InfoSecond", {
            profil: this.state.photoUri,
            id: this.state.id
        })
        return true;
    }

    fetchSante = async () => {
        console.log('fetch sante second de ', this.state.id)
        await axios.get(`${Bdd.api_url_second}/fiche-sante/list?idSecondUser=${this.state.id}`)
          .then(async res => {
            if (await !res) {
              console.log("tena misy olana")
            } else {
              const fiche = res.data.data
              console.log('resultat de fiche sante ', fiche)
              if (res.data === null) this.setState({ isFirst: true })
              else this.setState({
                isFirst: false,
                blood: fiche.groupeSanguin, size: fiche.taille,
                weight: fiche.poids, medecin: fiche.medecinTraitant,
                secu: fiche.numSecu, donate: fiche.donnateur,
                idFiche: fiche.idFiche, allergies: fiche.allergies
              })
            }
          })
          .catch(err => {
            console.log(err)
          })
      }

    async getCameraPermissions () {
        const { status } = await Permissions.askAsync(Permissions.CAMERA_ROLL)
        if (status !== 'granted') {
            Alert('permission not granteed')
            this.setState({
                rollGranted: false,
                cameraGranted: false
            })
        } else {
            this.setState({
                rollGranted: true,
                cameraGranted: true
            })
        }
    }

    async _pickImage () {
        // launchCameraAsync
        const result = await ImagePicker.launchImageLibraryAsync({
            allowsEditing: true,
            base64: true,
            quality: 0.5
        })

        if (result.cancelled) {
            return
        }

        this._handleImagePicked(result)
    }

    async _handleImagePicked (pickerResult) {
        let uploadUrl = ''
        try {
            this.setState({ isLoading: true })

            if (!pickerResult.cancelled) {
                uploadUrl = await this.uploadImageAsync(pickerResult.uri)
                const data = {
                    imageUser: uploadUrl,
                    idUser: this.props.user.user.idUser
                }
                this.saveProfileImageInfo(data)
            }
        } catch (e) {
            console.log(e)
            alert('Upload failed, sorry :(')
        } finally {
            this.setState({ isLoading: false })
            /* this.props.navigation.navigate("MonProfil") */
            this.setState({ photoUri: uploadUrl })
        }
    }

    saveProfileImageInfo (data) {
        this.props.ModifyPhoto(data)
    }

    async uploadImageAsync (uri) {
        // Why are we using XMLHttpRequest? See:
        // https://github.com/expo/expo/issues/2402#issuecomment-443726662
        const blob = await new Promise((resolve, reject) => {
            const xhr = new XMLHttpRequest()
            xhr.onload = function () {
                resolve(xhr.response)
            }
            xhr.onerror = function (e) {
                console.log(e)
                reject(new TypeError('Network request failed'))
            }
            xhr.responseType = 'blob'
            xhr.open('GET', uri, true)
            xhr.send(null)
        })
        const id = Math.random().toString()
        const ref = firebase
            .storage()
            .ref()
            .child(id)
        const snapshot = await ref.put(blob)

        // We're done with the blob, close and release it
        blob.close()

        return await snapshot.ref.getDownloadURL()
    }

    async takePdf() {
        let result = await DocumentPicker.getDocumentAsync({
            type: 'application/pdf',
            copyToCacheDirectory: false
        });
        console.log(result.type);
        console.log(result.name);
        console.log(result.uri);
        console.log(result.size);
        this.setState({
            name_pdf: result.name.substring(0,8)
        })
        this._handlePdfPicked(result);
    }

    _pickPdf = async () => {
        let result = await ImagePicker.launchImageLibraryAsync({
          allowsEditing : true,
          mediaTypes: ImagePicker.MediaTypeOptions.All,
        });
    
        //console.log(result)
    
        this._handlePdfPicked(result);
      }

      _handlePdfPicked = async pickerResult => {
        let uploadUrl =""
        try {
          this.setState({ isLoading: true });
    
          if (!pickerResult.cancelled) {
            uploadUrl = await this.uploadPdfAsync(pickerResult.uri);
            this.setState({uri_pdf : uploadUrl})

          }
        } catch (e) {
          console.log(e);
          alert('Upload failed, sorry :(');
        } finally {
          this.setState({ isLoading: false });
          /* this.props.navigation.navigate("MonProfil") */
          this.setState({photo_pdf : uploadUrl})
          console.log('link share pdf ', this.state.photo_pdf);
        }
      }

      uploadPdfAsync = async (uri)  =>{
        // Why are we using XMLHttpRequest? See:
        // https://github.com/expo/expo/issues/2402#issuecomment-443726662
        const blob = await new Promise((resolve, reject) => {
          const xhr = new XMLHttpRequest();
          xhr.onload = function() {
            resolve(xhr.response);
          };
          xhr.onerror = function(e) {
            console.log(e);
            reject(new TypeError('Network request failed'));
          };
         
       xhr.responseType = 'blob';
          xhr.open('GET', uri, true);
          xhr.send(null);
        });
        const id = Math.random().toString()
        const ref = firebase
          .storage()
          .ref()
          .child(id);
        const snapshot = await ref.put(blob);
      
        // We're done with the blob, close and release it
        blob.close();
      
        return await snapshot.ref.getDownloadURL();
      }

      shareLink() {
        if(this.state.photo_pdf === 'vide') {
            Alert.alert('Info', 'Veuillez choisir un fichier pdf', [
                {
                    text: 'OK',
                    onPress: () => {}
                }
            ])
            return null;
        }
        const url = this.state.photo_pdf;
        Share.share({title: 'Fiche Santé', message: url}).then(
            Alert.alert('Succes', 'Fichier Partagé', [
                {
                    text: 'OK',
                    onPress: () => {}
                }
            ])
        ).catch(
            err => Alert.alert('Echec', 'Erreur lors de partage de lien', [
                {
                    text: 'OK',
                    onPress: () => {}
                }
            ])
        )
    }

    renderHeader_1 = () => {
        return (
            <View style={styles.main_profil}>
                <View style={styles.under_main_profil_1}>
                <Avatar
                size={100}
                rounded
