    createTaille () {
        const taille = []
        for (let i = 50; i <= 210; i++) {
            const item = {
                label: i + ' cm',
                value: i
            }
            taille.push(item)
        }
        return taille
    }

    createPoids () {
        const poids = []
        for (let i = 1; i <= 180; i++) {
            const item = {
                label: i + ' kg',
                value: i
            }
            poids.push(item)
        }
        return poids
    }

