                </Text>
              </View>


              <TouchableOpacity style={[styles.buttonContainer, styles.signupButton]} onPress={() => this.register()}>
                <Text style={styles.signUpText}>S'inscrire {"  "}</Text>
              </TouchableOpacity>

          </View>

        </View >
      </View>
      </ScrollView>
    );
  }
}
