import React, { Component } from 'react'
import { Text, View, StyleSheet,TouchableHighlight } from 'react-native'
import { connect } from 'react-redux'
import TopMenu from "../../component/Menu/TopMenu"
import BottomMenu from "../../component/Menu/BottomMenu"
import { TouchableOpacity } from 'react-native-gesture-handler'
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import { faHome,faEdit,  faBars, faTimes, faCaretDown, faNotesMedical, faHandsHelping, faPumpMedical, faDisease, faTrash, faEye } from '@fortawesome/free-solid-svg-icons';
import { Avatar, Divider,ListItem } from 'react-native-elements';

import axios from 'axios'
import Bdd from "../../API/Bdd"
import {
    widthPercentageToDP as wp,
    heightPercentageToDP as hp,
  } from 'react-native-responsive-screen';
  import moment from 'moment';

  
export class Reminder extends Component {
    
    constructor(props){
        super(props)
        this.state = {
            rappels : [],
            selectedType : "all",
            numColored : 0,

            matin: false,
            midi: false,
            soir: false,

            monday: false,
            tuesday: false,
            wednesday: false,
            thirsday: false,
            friday: false,
            saturday: false,
            sunday: false,

            name: "",
            time: new Date(),
            matinHour: null,
            midiHour: null,
            soirHour: null,
            type: "matin",
            isOpenTime: false
            
        }
    }
    componentDidMount(){
        console.log(this.state.selectedType)
        this._initRappel()
    }    
    renderRappelType = (text,isColor,type,index) =>(
        <TouchableOpacity  key={index} onPress={()=> {this.handleType(type,index)}} style={[styles.cardContainer, isColor == true ? {backgroundColor : "#008ac2"} : {backgroundColor : "white"}]}>
                <Text style={[styles.rappelText, isColor == true ? {backgroundColor : "#008AC8",  borderRadius : 5} : {backgroundColor : "#00C1B4"}]}>{text}</Text>
        </TouchableOpacity>
    )

    handleType =async  (type,index) =>{
        
        await this.setState({
            selectedType  : type,
            numColored : index
        })

        this._initRappel()
    }
    
  
    _initRappel = () =>{
       
        let data = {
            user_id: this.props.second.second_users[this.props.second.indexSelected].id
        }


        axios.post(Bdd.api_url+"/api/rappel/one",  data)
            .then((response)=>{
                   
                if(this.state.selectedType == "all") {
                    
                    this.setState({rappels: response.data.rappels })
                }else{
                    let selectRappel  = response.data.rappels.filter(el =>{
                       
                        return el.name.split("__")[1] === this.state.selectedType
                    })
                    this.setState({rappels: selectRappel })
                }
                
               console.log(this.state.rappels)
                
        })
    }
    
    getMonthValue(value){
        switch(value) {
            case 1 : return  "JAN"
             break
             case 2 : return "FEV"
             break
             case 3 : return "MAR"
             break
             case 4 : return "AVR"
             break
             case 5 : return "MAI"
             break
             case 6 : return "JUN"
             break
             case 7 : return "JUL"
             break
             case 8 : return "AOU"
             break
             case 9 : return "SEP"
             break
             case 10 : return "OCT"
             break
             case 11 : return "NOV"
             break
             case 12 : return "DEV"
             break
        }
    }
