        let data = {
            id: this.state.rappels[index].id
        }

        let rappels = this.state.rappels;

        rappels.splice(index, 1);

        axios.post(Bdd.api_url+"/api/rappel/delete",  data)
        .then((response)=>{
      
            if(response.data.status === 200) {
                this.setState({rappels: rappels })
            }
        })
    }
    goToDetail = (id) => {
        this.props.navigation.navigate("RappelDetail", {id: id})

    }

    getDays(data){
        lun = data.monday === 1 ? "Lun" : ""
        mar = data.tuesday ===1 ? "Mar" : ""
        mer = data.wednesday ===1 ? "Mer" : ""
        jeu = data.thirsday ===1 ? "Jeu" :""
        ven = data.friday ===1 ? "Ven" : ""
        sam = data.saturday ===1 ? "Sam":""
        dim = data.sunday ===1 ? "Dim" : ""

        return lun+" "+mar+" "+mer+" "+jeu+" "+ven+" "+sam+" "+dim
    }

    render() {
       
        const datas = [
