                        </TouchableOpacity>
                        </View>
                        <View style={styles.appelType}>
                            {datas.map((data,i) =>{
                                return this.state.numColored === i ? this.renderRappelType(data.type,true,data.label,i) : this.renderRappelType(data.type,false,data.label,i)
                            }) }
                        </View>
                    </View>

                    <View style={styles.itemContainer}>
                       
                       {this.state.rappels !== null && this.state.rappels.map((r,index) =>{
                           let name = r.name.split("__")[0];
                           let date = r.name.split("__")[2].split("-")[0];

                           let month = this.getMonthValue(parseInt(r.name.split("__")[2].split("-")[1]))
                           let matinHour = r.matinHour !== null ? r.matinHour : ""
                           let midiHour = r.midiHour !== null ? r.midiHour : ""
                           let soirHour = r.soirHour !== null ? r.soirHour : ""
                           let h = matinHour+"/ "+midiHour+"/ "+soirHour
                           const jour = this.getDays(r)
                          
                           return <ListItem
                           containerStyle={{margin : 10}}
                           key={index}
                           title={name}
                           subtitle={jour}
                           leftAvatar={{
                               
                               title: date
                           }}
                           bottomDivider
                           rightElement={
                          <React.Fragment>
                               <TouchableOpacity style={{padding : 5, }} onPress={()=> this.props.navigation.navigate("RappelDetail",{id : r.id})}>
                               <FontAwesomeIcon icon={faEye} />
                           </TouchableOpacity>
                           <TouchableOpacity style={{padding : 5, }} onPress={()=>this.onClickDelete(index)}>
                           <FontAwesomeIcon icon={faTrash} />
                       </TouchableOpacity>
                          </React.Fragment>
                               }
                       />
                           
                       })}
                        
                        
                    </View>

                </View>

            </View>
        )
    }

    
}

