import React, { Component } from 'react'
import { Card, Icon, Input, } from 'react-native-elements'
import {
  Image,
  ImageBackground,
  Linking,
  ListView,
  Platform,
  ScrollView,
  StyleSheet,
  Text,
  View,
  Slider,
  Switch,
  ActivityIndicator
} from 'react-native'
import PropTypes from 'prop-types'
import { FlatList, TouchableOpacity } from 'react-native-gesture-handler'
import { connect } from 'react-redux'
import { deleteContact, modifyUserInfo, setIndexSelected, setSecondInfo } from '../../Action';
import { Avatar } from 'react-native-elements';
import TopMenu from "../../component/Menu/TopMenu"
import * as firebase from 'firebase';
import firestore from 'firebase/firestore'
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome'
import { faEdit } from '@fortawesome/free-solid-svg-icons'
import * as Permissions from 'expo-permissions'
import * as ImagePicker from 'expo-image-picker'
import RadioForm, { RadioButton, RadioButtonInput, RadioButtonLabel } from 'react-native-simple-radio-button';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
} from 'react-native-responsive-screen'
const DEFAUTL_USER = "https://www.nehome-groupe.fr/wp-content/uploads/2015/09/image-de-profil-2.jpg"
class MySante extends Component {

  constructor(props) {
    super(props)
    this.state = {
      isSelectedProfile: false,
      photoUri: "", id: this.props.second.second_users[this.props.second.indexSelected].id,


      blood: this.props.second.second_users[this.props.second.indexSelected].blood,
      size: this.props.second.second_users[this.props.second.indexSelected].size,
      weight: this.props.second.second_users[this.props.second.indexSelected].weight,
      donate: this.props.second.second_users[this.props.second.indexSelected].donate === null ? "Non" : this.props.second.second_users[this.props.second.indexSelected].donate,
      secu: this.props.second.second_users[this.props.second.indexSelected].secu,

      id: this.props.second.second_users[this.props.second.indexSelected].id,

      photoUri: "",

      medecin: "",
      allergie: "",
      traitement: "",

      isMedicinExist: false,
      isAllergieExist: false,
      isTraitementExist: false,

      isLoading: false,

      isModifbegin: false

    }
    this.ref = firebase.firestore().collection('profile');

  }

  componentDidMount = async () => {

    this.setState({ isLoading: true })
    await this.fetchImage()
    await this.fetchAutre()
    
    this.setState({ isLoading: false })
  }

  async fetchAutre() {
    await firebase.firestore().collection("medecin").where("idUser", "==", this.props.second.second_users[this.props.second.indexSelected].id).orderBy("created_at","desc").limit(1).get()
      .then(querySnapshot => {
        querySnapshot.forEach(doc => {
          doc.data().nomMed === "" ? this.setState({ isMedicinExist: true }) : ""
          this.setState({ medecin: doc.data().nomMed })
        })
      })

    await firebase.firestore().collection("allergie").where("idUser", "==", this.props.second.second_users[this.props.second.indexSelected].id).orderBy("created_at","desc").limit(1).get()
      .then(querySnapshot => {
        querySnapshot.forEach(doc => {
          doc.data().nomAll === "" ? this.setState({ isAllergieExist: true }) : ""
          this.setState({ allergie: doc.data().nomAll })
        })
      })

    await firebase.firestore().collection("traitement").where("idUser", "==", this.props.second.second_users[this.props.second.indexSelected].id).orderBy("created_at","desc").limit(1).get()
      .then(querySnapshot => {
        querySnapshot.forEach(doc => {
          doc.data().traitement === "" ? this.setState({ isTraitementExist: true }) : ""

          this.setState({ traitement: doc.data().traitement })
        })
      })
  }

  fetchImage = async () => {
    await firebase.firestore().collection('profile')
      .where("idUser", "==", this.state.id)
      .get()
      .then(querySnapshot => {
        querySnapshot.forEach(doc => {
        
          this.setState({
            photoUri: doc.data().photoUri
          })

        })

      })
      .catch(err => {
        console.log(err)
      })
  }



  renderHeader = () => {
    
    return (
      <View style={styles.headerContainer}>
        <View style={Platform.OS === 'ios' ? styles.under_ios : styles.under}>
          <TopMenu navigation={this.props.navigation} />
        </View>

        <ImageBackground
          style={styles.headerBackgroundImage}
          blurRadius={10}
          source={{ uri: this.state.photoUri == "" ? DEFAUTL_USER : this.state.photoUri }}
        >
          <View style={styles.headerColumn}>

            <Image
              style={styles.userImage}
              source={{ uri: this.state.photoUri == "" ? DEFAUTL_USER : this.state.photoUri }}
            />
            <Text style={styles.userNameText}>{this.props.second.second_users[this.props.second.indexSelected].firstName} {this.props.second.second_users[this.props.second.indexSelected].lastName} </Text>
            <View style={styles.userAddressRow}>
              <View style={styles.actionBtn}>
                <TouchableOpacity onPress={() => this.props.navigation.navigate("MySecondProfil")}>
                  <Avatar size="medium" rounded overlayContainerStyle={{ backgroundColor: "#008AC8" }} icon={{ name: 'share', type: 'font-awesome' }} />
                </TouchableOpacity>
              </View>
              {this.state.isModifbegin === false &&
                <View style={styles.actionBtn}>
                  <TouchableOpacity onPress={() => this.setState({ isModifbegin: !this.state.isModifbegin })}>
                    <Avatar size="medium" rounded overlayContainerStyle={{ backgroundColor: "#008AC8" }} icon={{ name: 'pencil', type: 'font-awesome' }} />
                  </TouchableOpacity>
                </View>
              }
              {this.state.isModifbegin === true &&
                <View style={styles.actionBtn}>
                  <TouchableOpacity onPress={async () => {
                    if (this.props.second.indexSelected === 0) {
                      await this.modifInfoPerso()

                    } else {
                      await this.modifySecondInfoPerso()

                    }
                  }}>
                    <Avatar size="medium" rounded overlayContainerStyle={{ backgroundColor: "#008AC8" }} icon={{ name: 'save', type: 'font-awesome' }} />
                  </TouchableOpacity>
                </View>
              }
              <View style={styles.actionBtn}>
                <TouchableOpacity onPress={() => this._pickImage()}>
                  <Avatar size="medium" rounded overlayContainerStyle={{ backgroundColor: "#008AC8" }} icon={{ name: 'camera', type: 'font-awesome' }} />
                </TouchableOpacity>
              </View>
            </View>
          </View>
        </ImageBackground>
      </View>
    )
  }


  modifInfoPerso() {
    this.setState({ isLoading: true })
    let userModified = {
      firstName: this.props.user.user.data.user[0].firstName,
      lastName: this.props.user.user.data.user[0].lastName,
      phone: this.props.user.user.data.user[0].phone,
      address: this.props.user.user.data.user[0].address,
      zip: this.props.user.user.data.user[0].zip,
      city: this.props.user.user.data.user[0].city,
      blood: this.props.user.user.data.user[0].blood,
      size: this.props.user.user.data.user[0].size,
      weight: this.props.user.user.data.user[0].weight,
      donate: this.props.user.user.data.user[0].donate,
      secu: this.props.user.user.data.user[0].secu,
      user_id: this.props.user.user.data.user[0].id
    }

    userModified.blood = this.state.blood
    userModified.size = this.state.size
    userModified.weight = this.state.weight
    userModified.secu = this.state.secu
    userModified.donate = this.state.donate

    console.log(userModified)
    this.saveAutre(this.state.medecin, this.state.allergie, this.state.traitement)
    this.props.modifyUserInfo(userModified)
    this.setState({ isLoading: false, isModifbegin: false })
  }


  saveAutre(med, all, traitement) {

    firebase.firestore().collection('medecin').add({
      idUser: this.props.second.second_users[this.props.second.indexSelected].id,
      nomMed: med,
      created_at: firebase.firestore.FieldValue.serverTimestamp()
    })


    firebase.firestore().collection('allergie').add({
      idUser: this.props.second.second_users[this.props.second.indexSelected].id,
      nomAll: all,
      created_at: firebase.firestore.FieldValue.serverTimestamp()
    })

    firebase.firestore().collection('traitement').add({
      idUser: this.props.second.second_users[this.props.second.indexSelected].id,
      traitement: traitement,
      created_at: firebase.firestore.FieldValue.serverTimestamp()
    })


  }


  modifySecondInfoPerso = async () => {
    this.setState({ isLoading: true })
    let userModified = {
      firstName: this.props.second.second_users[this.props.second.indexSelected].firstName,
      lastName: this.props.second.second_users[this.props.second.indexSelected].lastName,
      phone: this.props.second.second_users[this.props.second.indexSelected].phone,
      address: this.props.second.second_users[this.props.second.indexSelected].address,
      zip: this.props.second.second_users[this.props.second.indexSelected].zip,
      city: this.props.second.second_users[this.props.second.indexSelected].city,
      blood: this.props.second.second_users[this.props.second.indexSelected].blood,
      size: this.props.second.second_users[this.props.second.indexSelected].size,
      weight: this.props.second.second_users[this.props.second.indexSelected].weight,
      donate: this.props.second.second_users[this.props.second.indexSelected].donate,
      secu: this.props.second.second_users[this.props.second.indexSelected].secu,
      id: this.props.second.second_users[this.props.second.indexSelected].id
    }

    userModified.blood = this.state.blood
    userModified.size = this.state.size
    userModified.weight = this.state.weight
    userModified.secu = this.state.secu
    userModified.donate = this.state.donate

    console.log(userModified)
    this.saveAutre(this.state.medecin, this.state.allergie, this.state.traitement)

    axios.post(Bdd.api_url + '/api/second_user/update', userModified)
      .then((response) => {

        if (response.data.status === 200) {

          AsyncStorage.getItem('bosToken')
            .then((token) => {
              let user_id = this.props.user.user.data.user[0].id
              this.props.setSecondInfo(token, user_id)
              this.setState({ isLoading: false, isModifbegin: false })
            })
        }
      })

  }




  render() {
    var groupeSanguin = [
      { label: 'A+', value: 0 },
      { label: 'A-', value: 1 },
      { label: 'B+', value: 2 },
      { label: 'B-', value: 3 },
      { label: 'AB+', value: 4 },
      { label: 'AB-', value: 5 },
      { label: 'O+', value: 6 },
      { label: 'O-', value: 7 }
    ];
    return (
      <ScrollView style={styles.scroll}>
        <View style={styles.container}>
          {this.state.isLoading && <View style={styles.loading_container}>
            <ActivityIndicator size="large" />
          </View>}

          <Card containerStyle={styles.cardContainer}>
            {this.renderHeader()}
          </Card>



          <View style={styles.infoContainer}>


            <View>
              <Text style={styles.labelText}>Groupe Sanguin</Text>
              {this.state.isModifbegin === true && <RadioForm
                formHorizontal={true}
                labelHorizontal={false}
                radio_props={groupeSanguin}
                initial={-1}
                onPress={(value) => { this.setState({ blood: groupeSanguin[value].label }) }}
              />

              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.blood}</Text>
              }
            </View>
            <View>
              <Text style={styles.labelText}>Taille</Text>
              {this.state.isModifbegin === true && <View>

                <Slider
                  style={styles.slider}
                  step={1}
                  minimumValue={50}
                  maximumValue={220}
                  onSlidingComplete={(value) => { this.setState({ size: value }) }}
                  value={parseInt(this.state.size)}
                  maximumTrackTintColor='#e2e0e0'
                  minimumTrackTintColor='#008AC8'
                  thumbTintColor='#008AC8'

                />
                <Text>{this.state.size} cm</Text>
              </View>

              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.size} cm</Text>
              }
            </View>




            <View>
              <Text style={styles.labelText}>Poids</Text>
              {this.state.isModifbegin === true && <View>
                <Slider
                  style={styles.slider}
                  step={1}
                  minimumValue={2}
                  maximumValue={150}
                  onSlidingComplete={(value) => { this.setState({ weight: value }) }}
                  value={parseInt(this.state.weight)}
                  maximumTrackTintColor='#e2e0e0'
                  minimumTrackTintColor='#008AC8'
                  thumbTintColor='#008AC8'

                />
                <Text >{this.state.weight} kg</Text>
              </View>
              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.weight} kg</Text>
              }
            </View>
            <View>
              <Text style={styles.labelText}>Donnateur d'organne</Text>
              {this.state.isModifbegin === true && <Switch
                trackColor={{ false: "#767577", true: "#81b0ff" }}
                thumbColor={this.state.donate ? "#f5dd4b" : "#f4f3f4"}
                ios_backgroundColor="#3e3e3e"
                onValueChange={(value) => {
                  if (value === true) this.setState({ donate: "Oui" })
                  else this.setState({ donate: "Non" })
                }}
                value={this.state.donate === "Non" ? false : true}
              />
              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.donate}</Text>
              }
            </View>
            <View>
              <Text style={styles.labelText}>Numro de la scurit sociale</Text>
              {this.state.isModifbegin === true && <Input
                keyboardType={"numeric"}
                onChangeText={(text) => this.setState({ secu: text })}
                value={this.state.secu}
              />
              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.secu}</Text>
              }
            </View>



            <View>
              <Text style={styles.labelText}>Mdecin traitant</Text>
              {this.state.isModifbegin === true && <Input

                onChangeText={(text) => this.setState({ medecin: text })}
                value={this.state.medecin}
              />
              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.medecin}</Text>
              }
            </View>

            <View>
              <Text style={styles.labelText}>Allergies</Text>
              {this.state.isModifbegin === true && <Input

                onChangeText={(text) => this.setState({ allergie: text })}
                value={this.state.allergie}
              />
              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.allergie}</Text>
              }
            </View>

            <View>
              <Text style={styles.labelText}>Traitements</Text>
              {this.state.isModifbegin === true && <Input

                onChangeText={(text) => this.setState({ traitement: text })}
                value={this.state.traitement}
              />
              }
              {this.state.isModifbegin === false &&
                <Text style={styles.labelValue}>{this.state.traitement}</Text>
              }
            </View>

          </View>



        </View>
      </ScrollView>
    )
  }
}
const styles = StyleSheet.create({
  infoContainer: {
    padding: 20
  },
  labelText: {
    fontWeight: "bold",
    fontSize: 16
  },
  labelValue: {
    color: "gray"
  },
  actionBtn: {
    position: "relative",
    top: -10,
    zIndex: 99999
  },
  slider: {
    width: wp('80%'),
    marginTop: hp("1%"),
    color: "#008AC8",

  },
  choiceItem: {
    backgroundColor: "white",
    padding: 10,
    margin: 10,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 2
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5
  },
  cardContainer: {
    backgroundColor: '#FFF',
    borderWidth: 0,
    flex: 1,
    margin: 0,
    padding: 0,
  },
  container: {
    flex: 1,
  },
  emailContainer: {
    backgroundColor: '#FFF',
    flex: 1,
    paddingTop: 30,
  },
  headerBackgroundImage: {
    paddingBottom: 20,
    paddingTop: 35,
  },
  headerContainer: {},
  headerColumn: {
    backgroundColor: 'transparent',
    ...Platform.select({
