import React, { Component } from 'react'
import { Text, View, StyleSheet, ScrollView, Linking } from 'react-native'
import TopMenu from "../../component/Menu/TopMenu"
import BottomMenu from "../../component/Menu/BottomMenu"
import { Avatar, Divider, ListItem } from 'react-native-elements';

import { TouchableOpacity } from 'react-native-gesture-handler';
import ActionButton from 'react-native-action-button';
import { addContact, deleteContact } from "../../Action"
import { connect } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-native-fontawesome';
import { faPhone, faTrash, faEye } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import Bdd from "../../API/Bdd"
class MyContact extends Component {

    constructor(){
        super()
        this.state = {
            contacts : []
        }

    }

    componentDidMount(){
        this.fetchContact()
    }

    fetchContact(){
        const idUser = this.props.user.user.idUser
        axios.get(`${Bdd.api_url}/contact/list?idUser=${idUser}`)
            .then(res =>{
                this.setState({contacts : res.data.data})
            })
    }
    
    callPerson(phone) {
        let url;
        if (Platform.OS === 'ios') {
            url = 'tel://' + phone
        }
        else if (Platform.OS === 'android') {
            url = 'tel:' + phone

        }

        Linking.canOpenURL(url)
            .then((supported) => {
                if (supported) {
                    return Linking.openURL(url)
                        .catch(() => null);
                }
            });

    }

    onClickDelete(id) {
        
        this.props.deleteContact(id);
        this.fetchContact()
    }

    render() {

        const list = [
            {
                title: 'Appointments',
                icon: 'av-timer'
            },
            {
                title: 'Trips',
                icon: 'flight-takeoff'
            },

        ]
        return (
            <View style={styles.container}>
                <View style={Platform.OS === 'ios' ? styles.under_ios : styles.under}>
                    <TopMenu navigation={this.props.navigation} />
                </View>
                <View style={styles.contactHeader}>
                    <View>

                        <Avatar rounded size="large" title="HR" />

                    </View>
                    <View>
                        <Text style={styles.contactHeaderText}>Mes contacts d'urgence</Text>
                    </View>
                </View>
                <Divider style={{ backgroundColor: 'grey' }} />
                <ScrollView>
                    { 
                        this.state.contacts.map((contact, i) => {
                            const iniital = contact.nomContact[0] + "" + contact.prenomContact[0]
                            return (
                                <ListItem
                                    containerStyle={{ margin: 10 }}
                                    key={i}
                                    title={contact.nomContact}
                                    subtitle={contact.emailContact}
                                    leftAvatar={{

                                        title: iniital
                                    }}
                                    bottomDivider
                                    rightElement={
                                        <React.Fragment>
                                            <TouchableOpacity style={{ padding: 10 }} onPress={() => this.props.navigation.navigate("ContactDetail",{contactId : contact.id})}>
                                                <FontAwesomeIcon icon={faEye} />
                                            </TouchableOpacity>
                                            <TouchableOpacity style={{ padding: 10 }} onPress={() => this.callPerson(contact.numContact)}>
                                                <FontAwesomeIcon icon={faPhone} />
                                            </TouchableOpacity>
                                            <TouchableOpacity style={{ padding: 10 }} onPress={() => this.onClickDelete(contact.idContact)}>
                                                <FontAwesomeIcon icon={faTrash} />
                                            </TouchableOpacity>
                                        </React.Fragment>
                                    }
                                /> )
                        })
                    } 

                </ScrollView>
                <ActionButton onPress={() => this.props.navigation.navigate("NewContact")} style={{ marginBottom: 5 }} buttonColor="#008AC8">

                </ActionButton>
            </View>
        )
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1
    },
    contactHeader: {
        flexDirection: "row",
        padding: 15,
        justifyContent: "space-around",
        alignItems: "center"
    },
    contactHeaderText: {
        fontWeight: "bold",
        fontSize: 20
    }

})

const mapStateToProps = (store) => {
    return {
        user: store.user,
        contact: store.contact,
        /* favorite: store.favorite,
        second: store.second */
    }
}

const mapDispatchToProps = {
    deleteContact
}

export default connect(mapStateToProps, mapDispatchToProps)(MyContact)
